// Application React enrichie avec UI moderne, navigation et animations
import React from "react";
import { useState } from "react";

import { motion } from "framer-motion";
import { Button } from "./components/ui/button";
import { Card } from "./components/ui/card";

export default function App() {
  const [view, setView] = useState("home");
  const [userItinerary, setUserItinerary] = useState([]);
  const [newDay, setNewDay] = useState("");
  const [sharedItineraries, setSharedItineraries] = useState([]);

  const handleAddDay = () => {
    if (newDay.trim()) {
      setUserItinerary([...userItinerary, newDay]);
      setNewDay("");
    }
  };

  const handleShareItinerary = () => {
    setSharedItineraries([
      ...sharedItineraries,
      {
        days: userItinerary,
        score: Math.floor(Math.random() * 100)
      }
    ]);
    setUserItinerary([]);
    setView("home");
  };

  return (
    <div className="min-h-screen bg-[url('https://www.transparenttextures.com/patterns/white-wall-3.png')] bg-cover font-sans">
      <header className="bg-white shadow-md py-4 sticky top-0 z-10">
        <div className="max-w-6xl mx-auto flex justify-between items-center px-6">
          <div className="flex items-center gap-4">
            <img src="https://cdn-icons-png.flaticon.com/512/854/854894.png" alt="Logo" className="w-10 h-10" />
            <h1 className="text-xl font-bold text-blue-700">Voyage Planner</h1>
            <nav className="flex gap-4 ml-6">
              <button onClick={() => setView("home")} className="hover:underline">Accueil</button>
              <button onClick={() => setView("explore")} className="hover:underline">Explorer</button>
              <button onClick={() => setView("compare")} className="hover:underline">Classement</button>
            </nav>
          </div>
          <div className="flex gap-2">
            <Button variant="outline">S'inscrire</Button>
            <Button>Login</Button>
          </div>
        </div>
      </header>

      <main className="max-w-5xl mx-auto p-6">
        {view === "home" && (
          <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} className="text-center mt-12">
            <img src="https://cdn-icons-png.flaticon.com/512/854/854894.png" alt="Travel" className="w-32 mx-auto mb-4" />
            <h2 className="text-2xl font-semibold text-blue-800">Bienvenue sur votre planificateur de voyage !</h2>
            <p className="text-gray-600 mt-2">Créez, partagez et comparez vos itinéraires facilement.</p>
          </motion.div>
        )}

        {view === "create" && (
          <motion.div initial={{ y: 20, opacity: 0 }} animate={{ y: 0, opacity: 1 }} className="mt-6">
            <Card className="p-6">
              <h2 className="text-xl font-bold text-blue-700 mb-4">Créer un itinéraire</h2>
              <input
                type="text"
                placeholder="Ajouter une activité pour le jour"
                value={newDay}
                onChange={(e) => setNewDay(e.target.value)}
                className="w-full border p-2 rounded mb-4"
              />
              <Button className="w-full mb-4" onClick={handleAddDay}>Ajouter le jour</Button>
              <ul className="list-disc pl-6 mb-4">
                {userItinerary.map((day, index) => (
                  <li key={index} className="text-gray-700">Jour {index + 1} : {day}</li>
                ))}
              </ul>
              <Button className="w-full bg-green-600 hover:bg-green-700" onClick={handleShareItinerary}>Partager l'itinéraire</Button>
            </Card>
          </motion.div>
        )}

        {view === "compare" && (
          <motion.div initial={{ y: 20, opacity: 0 }} animate={{ y: 0, opacity: 1 }} className="mt-6">
            <Card className="p-6">
              <h2 className="text-xl font-bold text-blue-700 mb-4">Classement des itinéraires</h2>
              {sharedItineraries.length === 0 ? (
                <p className="text-gray-500">Aucun itinéraire partagé pour l'instant.</p>
              ) : (
                sharedItineraries.map((itinerary, idx) => (
                  <div key={idx} className="border-b py-3">
                    <p className="font-semibold text-gray-800">Itinéraire {idx + 1}</p>
                    <p className="text-sm">Jours : {itinerary.days.length}</p>
                    <p className="text-sm">Score originalité : {itinerary.score} / 100</p>
                  </div>
                ))
              )}
            </Card>
          </motion.div>
        )}

        {view === "explore" && (
          <motion.div initial={{ y: 20, opacity: 0 }} animate={{ y: 0, opacity: 1 }} className="mt-6">
            <Card className="p-6">
              <h2 className="text-xl font-bold text-blue-700 mb-4">Explorer les itinéraires</h2>
              <p className="text-gray-600">Fonctionnalité en cours de développement...</p>
            </Card>
          </motion.div>
        )}
      </main>
    </div>
  );
}
