import React, { useState } from "react";
import styles from "./App.module.css";
import logo from "./assets/logo.png"; // assure-toi que ce chemin correspond bien à ton image

export default function App() {
  const [view, setView] = useState("home");

  return (
    <div className={styles.app}>
      {/* HEADER */}
      <header className={styles.header}>
        <div className={styles.left}>
          <img src={logo} alt="logo" className={styles.logo} />
          <h1 className={styles.brand}>Voyage Planner</h1>
        </div>
        <nav className={styles.nav}>
          <a href="#" onClick={() => setView("home")}>Accueil</a>
          <a href="#" onClick={() => setView("explore")}>Explorer</a>
          <a href="#" onClick={() => setView("compare")}>Classement</a>
        </nav>
        <div className={styles.right}>
          <button className={styles.signupBtn}>S'inscrire</button>
        </div>
      </header>

      {/* MAIN */}
      <main className={styles.main}>
        {view === "home" && (
          <>
            <img
              src={logo}
              alt="Illustration itinéraire"
              className={styles.mainImage}
            />
            <h2 className={styles.title}>Voyage Planner</h2>
            <p className={styles.subtitle}>
              A planning app used to create, compare and explore travel itineraries.
            </p>
            <div className={styles.actions}>
              <button onClick={() => setView("create")}>Créer</button>
              <button onClick={() => setView("compare")}>Comparer</button>
              <button onClick={() => setView("explore")}>Explorer</button>
            </div>
          </>
        )}

        {view === "create" && (
          <div className={styles.section}>
            <h2>Création d’un itinéraire</h2>
            <p>Ici, tu pourras ajouter des jours et des étapes.</p>
          </div>
        )}

        {view === "compare" && (
          <div className={styles.section}>
            <h2>Comparaison d’itinéraires</h2>
            <p>Ici, tu verras les itinéraires partagés et leurs scores.</p>
          </div>
        )}

        {view === "explore" && (
          <div className={styles.section}>
            <h2>Explorer les itinéraires</h2>
            <p>Découvre des idées de voyage proposées par d’autres utilisateurs.</p>
          </div>
        )}
      </main>
    </div>
  );
}
