// Application React inspirée du prototype Figma Low-Fidelity
import React from "react";
import { useState } from "react";

export default function App() {
  const [view, setView] = useState("home");
  const [userItinerary, setUserItinerary] = useState([]);
  const [newDay, setNewDay] = useState("");
  const [sharedItineraries, setSharedItineraries] = useState([]);

  const handleAddDay = () => {
    if (newDay.trim()) {
      setUserItinerary([...userItinerary, newDay]);
      setNewDay("");
    }
  };

  const handleShareItinerary = () => {
    setSharedItineraries([
      ...sharedItineraries,
      {
        days: userItinerary,
        score: Math.floor(Math.random() * 100)
      }
    ]);
    setUserItinerary([]);
    setView("home");
  };

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <header className="mb-6">
        <h1 className="text-2xl font-bold text-center">Voyage Planner</h1>
        <div className="flex justify-center gap-4 mt-4">
          <button onClick={() => setView("create")} className="bg-blue-500 text-white px-4 py-2 rounded">Créer</button>
          <button onClick={() => setView("compare")} className="bg-green-500 text-white px-4 py-2 rounded">Comparer</button>
          <button onClick={() => setView("explore")} className="bg-gray-700 text-white px-4 py-2 rounded">Explorer</button>
        </div>
      </header>

      {view === "create" && (
        <div className="bg-white shadow rounded p-4 max-w-md mx-auto">
          <h2 className="text-xl font-semibold mb-4">Créer un itinéraire</h2>
          <input
            type="text"
            placeholder="Ajouter une activité"
            value={newDay}
            onChange={(e) => setNewDay(e.target.value)}
            className="w-full border p-2 rounded mb-2"
          />
          <button onClick={handleAddDay} className="w-full bg-blue-500 text-white p-2 rounded mb-2">Ajouter le jour</button>
          <ul className="list-disc list-inside mb-2">
            {userItinerary.map((day, index) => (
              <li key={index}>Jour {index + 1} : {day}</li>
            ))}
          </ul>
          <button onClick={handleShareItinerary} className="w-full bg-green-500 text-white p-2 rounded">Partager l'itinéraire</button>
        </div>
      )}

      {view === "compare" && (
        <div className="bg-white shadow rounded p-4 max-w-2xl mx-auto">
          <h2 className="text-xl font-semibold mb-4">Comparaison d'itinéraires</h2>
          {sharedItineraries.length === 0 ? (
            <p className="text-gray-600">Aucun itinéraire partagé.</p>
          ) : (
            sharedItineraries.map((itinerary, idx) => (
              <div key={idx} className="border-b py-2">
                <p className="font-bold">Itinéraire {idx + 1}</p>
                <p>Nombre de jours : {itinerary.days.length}</p>
                <p>Score : {itinerary.score}/100</p>
              </div>
            ))
          )}
        </div>
      )}

      {view === "explore" && (
        <div className="bg-white shadow rounded p-4 max-w-2xl mx-auto">
          <h2 className="text-xl font-semibold mb-4">Explorer les itinéraires</h2>
          <p className="text-gray-600">Fonctionnalité à venir...</p>
        </div>
      )}
    </div>
  );
}
