// Mise √† jour : Am√©lioration compl√®te de la page Cr√©er avec titre, suppression et feedback
import React, { useState } from "react";
import styles from "./App.module.css";
import logo from "./assets/logo.png";

// üîΩ Composants externes
import Footer from "./components/Footer";
import FeaturedTrips from "./components/FeaturedTrips";

export default function App() {
  const [view, setView] = useState("home");
  const [newDay, setNewDay] = useState("");
  const [itineraryTitle, setItineraryTitle] = useState("");
  const [currentItinerary, setCurrentItinerary] = useState([]);
  const [sharedItineraries, setSharedItineraries] = useState([]);
  const [user, setUser] = useState(null);
  const [authView, setAuthView] = useState(null);
  const [username, setUsername] = useState("");
  const [showSuccess, setShowSuccess] = useState(false);

  const exampleItineraries = [
    {
      days: ["Visite du mus√©e", "Balade en for√™t", "Soir√©e barbecue"],
      user: "Aventureuse Anna",
      score: 82,
    },
    {
      days: ["Plage", "Plong√©e", "Cocktail en bord de mer"],
      user: "Tropical Tom",
      score: 91,
    },
  ];

  const handleAddDay = () => {
    if (newDay.trim()) {
      setCurrentItinerary([...currentItinerary, newDay]);
      setNewDay("");
    }
  };

  const handleDeleteDay = (index) => {
    const updated = [...currentItinerary];
    updated.splice(index, 1);
    setCurrentItinerary(updated);
  };

  const handleShare = () => {
    const score = Math.min(100, 60 + currentItinerary.length * 10);
    setSharedItineraries([
      ...sharedItineraries,
      {
        id: Date.now(),
        title: itineraryTitle || "Itin√©raire sans titre",
        days: currentItinerary,
        user: user || "Anonyme",
        score,
        date: new Date().toLocaleDateString(),
      },
    ]);
    setItineraryTitle("");
    setCurrentItinerary([]);
    setShowSuccess(true);
    setTimeout(() => setShowSuccess(false), 3000);
    setView("compare");
  };

  const handleLogin = () => {
    if (username.trim()) {
      setUser(username);
      setAuthView(null);
    }
  };

  const handleLogout = () => {
    setUser(null);
    setView("home");
  };

  const handleDelete = (id) => {
    setSharedItineraries(sharedItineraries.filter((it) => it.id !== id));
  };

  const handleEdit = (id) => {
    const itinerary = sharedItineraries.find((it) => it.id === id);
    if (itinerary) {
      setCurrentItinerary(itinerary.days);
      setItineraryTitle(itinerary.title || "");
      setSharedItineraries(sharedItineraries.filter((it) => it.id !== id));
      setView("create");
    }
  };

  return (
    <div className={styles.app}>
      <header className={styles.header}>
        <div className={styles.left}>
          <img src={logo} alt="logo" className={styles.logo} />
          <h1 className={styles.brand}>Travel Rank</h1>
        </div>
        <nav className={styles.nav}>
          <a href="#" onClick={() => setView("home")}>Accueil</a>
          <a href="#" onClick={() => setView("explore")}>Explorer</a>
          <a href="#" onClick={() => setView("compare")}>Classement</a>
          <a href="#" onClick={() => setView("profile")}>Profil</a>
        </nav>
        <div className={styles.right}>
          {user ? (
            <>
              <span>üëã {user}</span>
              <button className={styles.signupBtn} onClick={handleLogout}>Se d√©connecter</button>
            </>
          ) : (
            <>
              <button className={styles.signupBtn} onClick={() => setAuthView("signup")}>S'inscrire</button>
              <button className={styles.signupBtn} onClick={() => setAuthView("login")}>Login</button>
            </>
          )}
        </div>
      </header>

      <main className={styles.main}>
        {authView && (
          <div className={styles.section}>
            <h2>{authView === "login" ? "Connexion" : "Inscription"}</h2>
            <input
              type="text"
              placeholder="Votre nom d'utilisateur"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
            <button onClick={handleLogin}>Valider</button>
          </div>
        )}

        {view === "create" && (
          <div className={styles.section}>
            <h2>Cr√©er mon itin√©raire</h2>
            <input
              type="text"
              placeholder="Titre de l‚Äôitin√©raire (ex: Week-end √† Rome)"
              value={itineraryTitle}
              onChange={(e) => setItineraryTitle(e.target.value)}
            />
            <div className={styles.dayInput}>
              <input
                type="text"
                placeholder="Ajouter une activit√© pour un jour"
                value={newDay}
                onChange={(e) => setNewDay(e.target.value)}
              />
              <button onClick={handleAddDay}>Ajouter</button>
            </div>
            <ul>
              {currentItinerary.map((day, idx) => (
                <li key={idx}>
                  Jour {idx + 1} : {day}
                  <button onClick={() => handleDeleteDay(idx)} style={{ marginLeft: '10px' }}>üóëÔ∏è</button>
                </li>
              ))}
            </ul>
            {currentItinerary.length > 0 && (
              <button onClick={handleShare}>Partager l'itin√©raire</button>
            )}
            {showSuccess && <p style={{ color: "green" }}>üéâ Itin√©raire partag√© avec succ√®s !</p>}
          </div>
        )}

        {/* Le reste de l'application reste inchang√© ici... */}

        {view === "home" && !authView && (
          <>
            <img src={logo} alt="Illustration" className={styles.mainImage} />
            <h2 className={styles.title}>Travel Rank</h2>
            <p className={styles.subtitle}>
              Une application pour cr√©er, partager et comparer vos itin√©raires de voyage avec la communaut√©.
            </p>
            <div className={styles.actions}>
              <button onClick={() => setView("create")}>Cr√©er</button>
              <button onClick={() => setView("compare")}>Comparer</button>
              <button onClick={() => setView("explore")}>Explorer</button>
            </div>
            <FeaturedTrips />
          </>
        )}
      </main>

      <Footer />
    </div>
  );
}
