// Application complète React avec styles et contenu inspirés du prototype Figma Low-Fidelity
import React from "react";
import { useState } from "react";

export default function App() {
  const [view, setView] = useState("home");
  const [userItinerary, setUserItinerary] = useState([]);
  const [newDay, setNewDay] = useState("");
  const [sharedItineraries, setSharedItineraries] = useState([]);

  const handleAddDay = () => {
    if (newDay.trim()) {
      setUserItinerary([...userItinerary, newDay]);
      setNewDay("");
    }
  };

  const handleShareItinerary = () => {
    setSharedItineraries([
      ...sharedItineraries,
      {
        days: userItinerary,
        score: Math.floor(Math.random() * 100)
      }
    ]);
    setUserItinerary([]);
    setView("home");
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-blue-100 to-blue-200 font-sans">
      <header className="bg-white shadow-md py-4">
        <div className="max-w-5xl mx-auto flex justify-between items-center px-6">
          <h1 className="text-2xl font-bold text-blue-700">Voyage Planner</h1>
          <nav className="flex gap-4">
            <button onClick={() => setView("create")} className="text-sm font-medium hover:underline">Créer</button>
            <button onClick={() => setView("compare")} className="text-sm font-medium hover:underline">Comparer</button>
            <button onClick={() => setView("explore")} className="text-sm font-medium hover:underline">Explorer</button>
          </nav>
        </div>
      </header>

      <main className="max-w-4xl mx-auto p-6">
        {view === "home" && (
          <div className="text-center mt-12">
            <img src="https://cdn-icons-png.flaticon.com/512/854/854894.png" alt="Travel" className="w-32 mx-auto mb-4" />
            <h2 className="text-xl font-semibold text-blue-800">Bienvenue sur votre planificateur de voyage !</h2>
            <p className="text-gray-600 mt-2">Créez, partagez et comparez vos itinéraires facilement.</p>
          </div>
        )}

        {view === "create" && (
          <div className="bg-white rounded shadow p-6 mt-6">
            <h2 className="text-xl font-bold text-blue-700 mb-4">Créer un itinéraire</h2>
            <input
              type="text"
              placeholder="Ajouter une activité pour le jour"
              value={newDay}
              onChange={(e) => setNewDay(e.target.value)}
              className="w-full border p-2 rounded mb-4"
            />
            <button onClick={handleAddDay} className="bg-blue-600 text-white px-4 py-2 rounded mb-4 w-full">Ajouter le jour</button>
            <ul className="list-disc pl-6 mb-4">
              {userItinerary.map((day, index) => (
                <li key={index} className="text-gray-700">Jour {index + 1} : {day}</li>
              ))}
            </ul>
            <button onClick={handleShareItinerary} className="bg-green-600 text-white px-4 py-2 rounded w-full">Partager l'itinéraire</button>
          </div>
        )}

        {view === "compare" && (
          <div className="bg-white rounded shadow p-6 mt-6">
            <h2 className="text-xl font-bold text-blue-700 mb-4">Comparaison des itinéraires</h2>
            {sharedItineraries.length === 0 ? (
              <p className="text-gray-500">Aucun itinéraire partagé pour l'instant.</p>
            ) : (
              sharedItineraries.map((itinerary, idx) => (
                <div key={idx} className="border-b py-3">
                  <p className="font-semibold text-gray-800">Itinéraire {idx + 1}</p>
                  <p className="text-sm">Jours : {itinerary.days.length}</p>
                  <p className="text-sm">Score originalité : {itinerary.score} / 100</p>
                </div>
              ))
            )}
          </div>
        )}

        {view === "explore" && (
          <div className="bg-white rounded shadow p-6 mt-6">
            <h2 className="text-xl font-bold text-blue-700 mb-4">Explorer les itinéraires</h2>
            <p className="text-gray-600">Fonctionnalité en cours de développement...</p>
          </div>
        )}
      </main>
    </div>
  );
}
