// Structure de base du projet React pour l'application de comparaison d'itinéraires
import React from "react";
import { useState } from "react";

export default function App() {
  const [itineraries, setItineraries] = useState([]);
  const [newDay, setNewDay] = useState("");
  const [userItinerary, setUserItinerary] = useState([]);

  const addDayToItinerary = () => {
    if (newDay.trim() !== "") {
      setUserItinerary([...userItinerary, newDay]);
      setNewDay("");
    }
  };

  const shareItinerary = () => {
    setItineraries([...itineraries, { days: userItinerary, score: Math.floor(Math.random() * 100) }]);
    setUserItinerary([]);
  };

  return (
    <div className="grid grid-cols-2 gap-4 p-6">
      {/* Création d'un itinéraire */}
      <div className="bg-white shadow-xl rounded-2xl p-4">
        <h2 className="text-xl font-bold mb-4">Créer un itinéraire</h2>
        <input
          type="text"
          placeholder="Ajouter une activité pour le jour"
          value={newDay}
          onChange={(e) => setNewDay(e.target.value)}
          className="w-full border rounded p-2 mb-2"
        />
        <button
          onClick={addDayToItinerary}
          className="bg-blue-500 text-white rounded p-2 w-full mb-2"
        >
          Ajouter le jour
        </button>
        <ul className="mb-4">
          {userItinerary.map((day, index) => (
            <li key={index} className="text-sm text-gray-700">
              Jour {index + 1} : {day}
            </li>
          ))}
        </ul>
        <button
          onClick={shareItinerary}
          className="bg-green-500 text-white rounded p-2 w-full"
        >
          Partager l'itinéraire
        </button>
      </div>

      {/* Comparaison d'itinéraires */}
      <div className="bg-white shadow-xl rounded-2xl p-4">
        <h2 className="text-xl font-bold mb-4">Comparaison des itinéraires</h2>
        {itineraries.length === 0 ? (
          <p className="text-gray-500">Aucun itinéraire partagé pour l'instant.</p>
        ) : (
          itineraries.map((itinerary, idx) => (
            <div
              key={idx}
              className="border-b border-gray-200 py-2 text-sm"
            >
              <p className="font-semibold">Itinéraire {idx + 1}</p>
              <p>Jours : {itinerary.days.length}</p>
              <p>Score originalité : {itinerary.score} / 100</p>
            </div>
          ))
        )}
      </div>
    </div>
  );
}
