// Application React compl√®te avec gestion d‚Äôutilisateur, badges et page profil
import React, { useState } from "react";
import styles from "./App.module.css";
import logo from "./assets/logo.png";

export default function App() {
  const [view, setView] = useState("home");
  const [newDay, setNewDay] = useState("");
  const [currentItinerary, setCurrentItinerary] = useState([]);
  const [sharedItineraries, setSharedItineraries] = useState([]);
  const [user, setUser] = useState(null);
  const [authView, setAuthView] = useState(null);
  const [username, setUsername] = useState("");

  const exampleItineraries = [
    {
      days: ["Visite du mus√©e", "Balade en for√™t", "Soir√©e barbecue"],
      user: "Aventureuse Anna",
      score: 82,
    },
    {
      days: ["Plage", "Plong√©e", "Cocktail en bord de mer"],
      user: "Tropical Tom",
      score: 91,
    },
  ];

  const handleAddDay = () => {
    if (newDay.trim()) {
      setCurrentItinerary([...currentItinerary, newDay]);
      setNewDay("");
    }
  };

  const handleShare = () => {
    const score = Math.min(100, 60 + currentItinerary.length * 10);
    setSharedItineraries([
      ...sharedItineraries,
      {
        days: currentItinerary,
        user: user || "Anonyme",
        score,
      },
    ]);
    setCurrentItinerary([]);
    setView("compare");
  };

  const handleLogin = () => {
    if (username.trim()) {
      setUser(username);
      setAuthView(null);
    }
  };

  return (
    <div className={styles.app}>
      <header className={styles.header}>
        <div className={styles.left}>
          <img src={logo} alt="logo" className={styles.logo} />
          <h1 className={styles.brand}>Voyage Planner</h1>
        </div>
        <nav className={styles.nav}>
          <a href="#" onClick={() => setView("home")}>Accueil</a>
          <a href="#" onClick={() => setView("explore")}>Explorer</a>
          <a href="#" onClick={() => setView("compare")}>Classement</a>
          <a href="#" onClick={() => setView("profile")}>Profil</a>
        </nav>
        <div className={styles.right}>
          {user ? (
            <span>üëã {user}</span>
          ) : (
            <>
              <button className={styles.signupBtn} onClick={() => setAuthView("signup")}>S'inscrire</button>
              <button className={styles.signupBtn} onClick={() => setAuthView("login")}>Login</button>
            </>
          )}
        </div>
      </header>

      <main className={styles.main}>
        {authView && (
          <div className={styles.section}>
            <h2>{authView === "login" ? "Connexion" : "Inscription"}</h2>
            <input
              type="text"
              placeholder="Votre nom d'utilisateur"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
            <button onClick={handleLogin}>Valider</button>
          </div>
        )}

        {view === "home" && !authView && (
          <>
            <img src={logo} alt="Illustration" className={styles.mainImage} />
            <h2 className={styles.title}>Voyage Planner</h2>
            <p className={styles.subtitle}>
              Une application pour cr√©er, partager et comparer vos itin√©raires de voyage avec la communaut√©.
            </p>
            <div className={styles.actions}>
              <button onClick={() => setView("create")}>Cr√©er</button>
              <button onClick={() => setView("compare")}>Comparer</button>
              <button onClick={() => setView("explore")}>Explorer</button>
            </div>
          </>
        )}

        {view === "create" && (
          <div className={styles.section}>
            <h2>Cr√©er mon itin√©raire</h2>
            <input
              type="text"
              placeholder="Ajouter une activit√© pour un jour"
              value={newDay}
              onChange={(e) => setNewDay(e.target.value)}
            />
            <button onClick={handleAddDay}>Ajouter</button>
            <ul>
              {currentItinerary.map((day, idx) => (
                <li key={idx}>Jour {idx + 1} : {day}</li>
              ))}
            </ul>
            {currentItinerary.length > 0 && (
              <button onClick={handleShare}>Partager l'itin√©raire</button>
            )}
          </div>
        )}

        {view === "compare" && (
          <div className={styles.section}>
            <h2>Classement des itin√©raires</h2>
            {[...sharedItineraries].sort((a, b) => b.score - a.score).map((itinerary, idx) => (
              <div key={idx} className={styles.itineraryCard}>
                <p><strong>{itinerary.user}</strong> - Score : {itinerary.score}/100 {itinerary.score > 90 && <span>üèÖ</span>}</p>
                <ul>
                  {itinerary.days.map((day, d) => (
                    <li key={d}>Jour {d + 1} : {day}</li>
                  ))}
                </ul>
              </div>
            ))}
          </div>
        )}

        {view === "explore" && (
          <div className={styles.section}>
            <h2>Explorer les itin√©raires populaires</h2>
            {exampleItineraries.map((itin, idx) => (
              <div key={idx} className={styles.itineraryCard}>
                <p><strong>{itin.user}</strong> - Score : {itin.score}/100 {itin.score > 90 && <span>üèÖ</span>}</p>
                <ul>
                  {itin.days.map((day, d) => (
                    <li key={d}>Jour {d + 1} : {day}</li>
                  ))}
                </ul>
              </div>
            ))}
          </div>
        )}

        {view === "profile" && user && (
          <div className={styles.section}>
            <h2>Mon Profil</h2>
            <p>Bienvenue <strong>{user}</strong> ! Voici vos informations :</p>
            <p>Itin√©raires partag√©s : {sharedItineraries.filter(i => i.user === user).length}</p>
            <p>Score moyen : {
              sharedItineraries.filter(i => i.user === user).reduce((acc, i) => acc + i.score, 0) / Math.max(1, sharedItineraries.filter(i => i.user === user).length)
            } / 100</p>
          </div>
        )}
      </main>
    </div>
  );
}
