// Application React avec page d'accueil restructurée et stylisée selon la capture
import React from "react";
import { useState } from "react";
import { Button } from "./components/ui/button";
import { Card } from "./components/ui/card";
import { motion } from "framer-motion";

export default function App() {
  const [view, setView] = useState("home");
  const [userItinerary, setUserItinerary] = useState([]);
  const [newDay, setNewDay] = useState("");
  const [sharedItineraries, setSharedItineraries] = useState([]);

  const handleAddDay = () => {
    if (newDay.trim()) {
      setUserItinerary([...userItinerary, newDay]);
      setNewDay("");
    }
  };

  const handleShareItinerary = () => {
    setSharedItineraries([
      ...sharedItineraries,
      {
        days: userItinerary,
        score: Math.floor(Math.random() * 100)
      }
    ]);
    setUserItinerary([]);
    setView("home");
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-100 via-white to-blue-50 font-sans">
      <header className="bg-white shadow-sm py-4 sticky top-0 z-10">
        <div className="max-w-7xl mx-auto flex justify-between items-center px-6">
          <div className="flex items-center gap-4">
            <img src="https://cdn-icons-png.flaticon.com/512/854/854894.png" alt="Logo" className="w-10 h-10" />
            <h1 className="text-xl font-bold text-blue-700">Voyage Planner</h1>
            <nav className="flex gap-6 ml-8">
              <button onClick={() => setView("home")} className="text-gray-600 hover:text-blue-600">Accueil</button>
              <button onClick={() => setView("explore")} className="text-gray-600 hover:text-blue-600">Explorer</button>
              <button onClick={() => setView("compare")} className="text-gray-600 hover:text-blue-600">Classement</button>
            </nav>
          </div>
          <div className="flex gap-3">
            <Button variant="outline">S'inscrire</Button>
            <Button>Login</Button>
          </div>
        </div>
      </header>

      <main className="max-w-6xl mx-auto p-6">
        {view === "home" && (
          <motion.div initial={{ opacity: 0, y: 20 }} animate={{ opacity: 1, y: 0 }} className="flex flex-col items-center text-center mt-20 space-y-6">
            <img src="https://cdn-icons-png.flaticon.com/512/854/854894.png" alt="Illustration" className="w-40 h-40 mb-4" />
            <h2 className="text-4xl font-extrabold text-blue-800">Planifiez votre voyage idéal</h2>
            <p className="text-gray-600 text-lg max-w-2xl">
              Créez votre itinéraire jour par jour, comparez-le avec vos amis et explorez les meilleures idées de voyages partagées par la communauté.
            </p>
            <div className="flex gap-6 mt-6">
              <Button onClick={() => setView("create")} className="bg-blue-600 hover:bg-blue-700 px-6 py-3 text-lg">Commencer</Button>
              <Button variant="outline" onClick={() => setView("explore")} className="px-6 py-3 text-lg">Explorer</Button>
            </div>
          </motion.div>
        )}

        {view === "create" && (
          <motion.div initial={{ y: 20, opacity: 0 }} animate={{ y: 0, opacity: 1 }} className="mt-10">
            <Card className="p-6">
              <h2 className="text-2xl font-bold text-blue-700 mb-4">Créer un itinéraire</h2>
              <input
                type="text"
                placeholder="Ajouter une activité pour le jour"
                value={newDay}
                onChange={(e) => setNewDay(e.target.value)}
                className="w-full border p-3 rounded mb-4"
              />
              <Button className="w-full mb-4" onClick={handleAddDay}>Ajouter le jour</Button>
              <ul className="list-disc pl-6 mb-4">
                {userItinerary.map((day, index) => (
                  <li key={index} className="text-gray-700">Jour {index + 1} : {day}</li>
                ))}
              </ul>
              <Button className="w-full bg-green-600 hover:bg-green-700" onClick={handleShareItinerary}>Partager l'itinéraire</Button>
            </Card>
          </motion.div>
        )}

        {view === "compare" && (
          <motion.div initial={{ y: 20, opacity: 0 }} animate={{ y: 0, opacity: 1 }} className="mt-10">
            <Card className="p-6">
              <h2 className="text-2xl font-bold text-blue-700 mb-4">Classement des itinéraires</h2>
              {sharedItineraries.length === 0 ? (
                <p className="text-gray-500">Aucun itinéraire partagé pour l'instant.</p>
              ) : (
                sharedItineraries.map((itinerary, idx) => (
                  <div key={idx} className="border-b py-3">
                    <p className="font-semibold text-gray-800">Itinéraire {idx + 1}</p>
                    <p className="text-sm">Jours : {itinerary.days.length}</p>
                    <p className="text-sm">Score originalité : {itinerary.score} / 100</p>
                  </div>
                ))
              )}
            </Card>
          </motion.div>
        )}

        {view === "explore" && (
          <motion.div initial={{ y: 20, opacity: 0 }} animate={{ y: 0, opacity: 1 }} className="mt-10">
            <Card className="p-6">
              <h2 className="text-2xl font-bold text-blue-700 mb-4">Explorer les itinéraires</h2>
              <p className="text-gray-600">Fonctionnalité en cours de développement...</p>
            </Card>
          </motion.div>
        )}
      </main>
    </div>
  );
}
